body{color:#000 !important;margin:0;padding:0
}
.content{max-width:1100px;margin:0 auto !important;padding:10px
}
.languages{background:#10cfbd;width:140px
}
.pointer{cursor:pointer
}
.oddBold:nth-child(odd){font-weight:bold
}
.anchor{text-decoration:underline;cursor:pointer
}
.anchor:hover{text-decoration:none
}
.anchor-simple{text-decoration:underline;cursor:pointer
}
.deslised{opacity:0;top:50px !important
}
.font-normal{font-weight:normal !important
}
.mask{width:100%;height:100%;position:absolute;left:0;top:0
}
.mask-transparent{background:transparent
}
.cleared{position:relative;z-index:4
}
.borderAnchor{border-bottom:2px solid yellow;cursor:pointer;line-height:0 !important
}
.borderAnchor:hover{border-bottom:2px solid transparent
}
.border1white{border:1px solid white
}
.my-badge{background:#fff;border:1px solid #ccc;margin:8px 11px;width:37px;height:37px
}
.my-badge-flex{background:#fff;border:1px solid #ccc;margin:8px 11px;padding:10px 30px;border:1px solid #eadddd
}
.my-btn{padding:6px 1em;color:#fff;border-radius:20px;font-size:.9em;-webkit-transition:.2s;-o-transition:.2s;transition:.2s;outline:0;border:0
}
.my-btn:active{-webkit-box-shadow:0 0 0  !important;box-shadow:0 0 0  !important;-webkit-transform:scale(0.95);-ms-transform:scale(0.95);transform:scale(0.95)
}
button.fixed{border-top:0 !important;border-left:0 !important;border-right:0 !important
}
.shadow-inset{-webkit-box-shadow:inset 0 0 10px;box-shadow:inset 0 0 10px #000
}
.icon-circled{width:40px !important;height:40px !important;border-radius:50%;border:2px solid yellow;font-size:28px
}
.my-btn-flex{padding:.4em .8em;border:0;outline:0;font-size:1.1em;width:100%;border-radius:.3em
}
.my-input{width:100%;border:2px solid #f1f1f1;outline:0;padding:.4em;font-size:1.15em;-webkit-transition:.3s;-o-transition:.3s;transition:.3s;border-radius:.2em;background:#fff;color:#333
}
.my-input:focus{border-color:yellow
}
.my-input-file{position:relative;color:white;margin:0px auto;width:150px;text-align:center
}
.my-input-file input{opacity:.1;top:0;left:0;z-index:-1;position:absolute
}
.my-input-file label{width:100%;height:100%;display:block;padding:10px 0;margin:0 !important;cursor:pointer
}
.border2{border:2px solid !important
}
.border-btn{border-bottom:3px inset rgba(0,0,0,0.3) !important;cursor:pointer
}
.border-btn:active{border-bottom:0px inset transparent !important;-webkit-transform:scale(0.97);-ms-transform:scale(0.97);transform:scale(0.97)
}
.my-select{position:relative;background:gray
}
.my-select select{-webkit-appearance:none;-moz-appearance:none;appearance:none;border:0;background:transparent;width:100%;min-height:50px;color:#fff;padding-left:.8em;position:relative;z-index:3;z-index-font-size:1.2em;cursor:pointer
}
.my-select select option{color:gray;font-size:1.2em;padding:.8em !important
}
